    private final int taskId;          // 任务ID
    private final String taskName;     // 任务名称
    private final String taskDesc;     // 任务描述
    private final String target;       // 适用目标
    private final String grade;        // 适用年级
    private final String timePoint;    // 适用时间点
    private final String taskType;    // 操作
    private final String operationDesc;// 操作描述
    private final String jumpUrl;     // 跳转链接

    // 构造方法
    TaskLibraryEnum(int taskId, String taskName, String taskDesc, String target, String grade, String timePoint, String taskType, String operationDesc, String jumpUrl) {
        this.taskId = taskId;
        this.taskName = taskName;
        this.taskDesc = taskDesc;
        this.target = target;
        this.grade = grade;
        this.timePoint = timePoint;
        this.taskType = taskType;
        this.operationDesc = operationDesc;
        this.jumpUrl = jumpUrl;
    }

    // 工具方法：根据任务ID获取枚举实例
    public static TaskLibraryEnum getByTaskId(int taskId) {
        for (TaskLibraryEnum task : values()) {
            if (task.taskId == taskId) {
                return task;
            }
        }
        return null;
    }


